import React from 'react'
import ReactDOM from 'react-dom'
import './Modal.css'

const Modal = ({show, onCloseModalButton, resorts}) => {
    const [form, setForm] = React.useState({
        option: "",
        resortName: "",
        longitude: "",
        latitude: "",
        delete: "",
    })

    const handleChange = e => {
        setForm({
            [e.target.name]: e.target.value,
        })
    }

    const submitForm = (event) => {
        event.preventDefault()
        console.log("submitting form", form)
    }

    if (!show) {
        return null
    }

    return ReactDOM.createPortal (
       <div className='modal-bg'>
            <div className='modal'>
                <form onSubmit={submitForm}>
                    <label>Modify: 
                        <select 
                        name="option"
                        value={form.option}
                        onChange={handleChange}
                        >
                            <option value="" disabled={true}>-- Choose an Option --</option>
                            <option value="Add">Add</option>
                            <option value="Delete">Delete</option>
                        </select>
                    </label>
                    {form.option === "" ? null :
                        form.option === "Add" ?
                            <div>
                                <label>Resort Name 
                                    <input 
                                    type="text" 
                                    name="resortName"
                                    value={form.resortName}
                                    onChange={handleChange}
                                    />
                                </label>
                                <br></br>
                                <label>Longitude
                                    <input 
                                    type="text" 
                                    name="longitude"
                                    value={form.longitude}
                                    onChange={handleChange}
                                    />
                                </label>
                                <br></br>
                                <label>Latitude
                                    <input 
                                    type="text" 
                                    name="latitude"
                                    value={form.latitude}
                                    onChange={handleChange}
                                    />
                                </label>
                            </div> :
                            <select 
                            name="delete"
                            value={form.delete}
                            onChange={handleChange}
                            >   
                                {[...resorts.keys()].map((item)=> {
                                    return <option key={item} value={item}>{item}</option>
                                })}
                            </select>
                    }
                    <button type='submit'>Submit</button>
                </form>
                <h2>heyy {form.option === "Add" ? "true" : "false"}</h2>
                <button onClick={onCloseModalButton}>Close Modal</button>
            </div>
        </div>
        , document.body
    )
}

export default Modal;
