import React from 'react'
import ReactDOM from 'react-dom'
import './Modal.css'

const Modal = ({show, onCloseModalButton, resorts}) => {
    const [form, setForm] = React.useState({
        //option: "",
        resortName: "",
        longitude: "",
        latitude: "",
        delete: "",
    })

    const [option, setOption] = React.useState("")

    const handleChange = e => {
        setForm({
            [e.target.name]: e.target.value,
        })
    }

    const handleOptionChange = e => {
        console.log(e)
        setOption(e)
    }

    const submitForm = (event) => {
        event.preventDefault()
        console.log("submitting form", form)
        if (form.option === "Add") {
            resorts.set(form.resortName, [form.latitude, form.longitude])
        }
    }

    if (!show) {
        return null
    }

    return ReactDOM.createPortal (
       <div className='modal-bg'>
            <div className='modal'>
                <form onSubmit={submitForm}>
                    <label>Modify: 
                        <select 
                        name="option"
                        value={option}
                        onChange={handleOptionChange}
                        >
                            <option value="" disabled={true}>-- Choose an Option --</option>
                            <option value="Add">Add</option>
                            <option value="Delete">Delete</option>
                        </select>
                    </label>
                    {form.option === "" ? null :
                        form.option === "Add" ?
                            <div>
                                <label>Resort Name 
                                    <input 
                                    type="text" 
                                    name="resortName"
                                    value={form.resortName}
                                    onChange={handleChange}
                                    />
                                </label>
                                <br></br>
                                <label>Longitude
                                    <input 
                                    type="text" 
                                    name="longitude"
                                    value={form.longitude}
                                    onChange={handleChange}
                                    />
                                </label>
                                <br></br>
                                <label>Latitude
                                    <input 
                                    type="text" 
                                    name="latitude"
                                    value={form.latitude}
                                    onChange={handleChange}
                                    />
                                </label>
                                <button type='submit'>Submit</button>
                            </div> :
                            <div>
                                <select 
                                name="delete"
                                value={form.delete}
                                onChange={handleChange}
                                >   
                                    {[...resorts.keys()].map((item)=> {
                                        return <option key={item} value={item}>{item}</option>
                                    })}
                                </select>
                                <button type='submit'>Submit</button>
                            </div>
                    }
                    <h2>test {form.option}</h2>
                </form>
                <button onClick={onCloseModalButton}>Close Modal</button>
            </div>
        </div>
        , document.body
    )
}

export default Modal;
