import React from 'react'
import ReactDOM from 'react-dom'
import './Modal.css'

const Modal = ({show, onCloseModalButton, resorts}) => {
    const [formState, setFormState] = React.useState({
        option: "",
        resortName: "",
        longitude: "",
        latitude: "",
    })

    const handleChange = e => {
        setFormState({
            [e.target.name]: e.target.value,
        })
    }

    if (!show) {
        return null
    }

    return ReactDOM.createPortal (
       <div className='modal-bg'>
            <div className='modal'>
                <form>
                    <label>Modify: 
                        <select 
                        name="option"
                        value={formState.option}
                        onChange={handleChange}
                        >
                            <option value="Add">Add</option>
                            <option value="Delete">Delete</option>
                        </select>
                    </label>
                    {formState.option === "Add" ?
                        <div>
                            <input 
                            type="text" 
                            name="resortName"
                            value={formState.resortName}
                            onChange={handleChange}
                            />
                            <input 
                            type="text" 
                            name="longitude"
                            value={formState.longitude}
                            onChange={handleChange}
                            />
                            <input 
                            type="text" 
                            name="latitude"
                            value={formState.latitude}
                            onChange={handleChange}
                            />
                        </div> :
                        <h2>{[...resorts.keys()][0]}</h2>
                    }
                </form>
                <h2>heyy {formState.option === "Add" ? "true" : "false"}</h2>
                <button onClick={onCloseModalButton}>Close Modal</button>
            </div>
        </div>
        , document.body
    )
}

export default Modal;
